import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, Link } from 'react-router-dom';
import axios from 'axios';

// Import Components
import RegisterComponent from './components/RegisterComponent';
import LoginComponent from './components/LoginComponent';
import ProfileComponent from './components/ProfileComponent';
import BetHistoryComponent from './components/BetHistoryComponent';
import StakeOutBet from './StakeOutBet';

// Authentication guard component
const PrivateRoute = ({ children }) => {
  const token = localStorage.getItem('token');
  if (!token) return <Navigate to="/login" replace />;
  return children;
};

// Main navbar component
const Navbar = ({ isAuthenticated, logout, user }) => (
  <nav className="navbar">
    <div className="navbar-brand">
      <Link to="/">Stake Out Bet</Link>
    </div>
    <div className="navbar-menu">
      {isAuthenticated ? (
        <>
          <div className="user-balance">
            Balance: ${parseFloat(user?.balance || 0).toFixed(2)}
          </div>
          <Link to="/profile">Profile</Link>
          <Link to="/history">Bet History</Link>
          <button onClick={logout} className="logout-btn">Logout</button>
        </>
      ) : (
        <>
          <Link to="/login">Login</Link>
          <Link to="/register">Register</Link>
        </>
      )}
    </div>
  </nav>
);

// Dashboard component with proper redirect
const Dashboard = () => {
  const user = JSON.parse(localStorage.getItem('user') || '{}');

  return (
    <div className="dashboard">
      <h2>Welcome, {user.username}!</h2>
      <p>Your balance: ${parseFloat(user.balance || 0).toFixed(2)}</p>

      <div className="dashboard-actions">
        <Link to="/profile" className="dashboard-card">
          <h3>Profile</h3>
          <p>Manage your account and settings</p>
        </Link>

        <Link to="/history" className="dashboard-card">
          <h3>Bet History</h3>
          <p>View your betting history and statistics</p>
        </Link>

        <Link to="/stakeout" className="dashboard-card betting-card">
          <h3>Place Bets</h3>
          <p>Start playing and win big!</p>
          <button className="play-btn">Play Now</button>
        </Link>
      </div>
    </div>
  );
};

// Main App component
const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    const storedUser = localStorage.getItem('user');

    if (token && storedUser) {
      setIsAuthenticated(true);
      setUser(JSON.parse(storedUser));
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
    }
  }, []);

  const logout = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios.post('http://localhost:4000/api/logout', {}, {
        headers: { Authorization: `Bearer ${token}` }
      });
    } catch (error) {
      console.error('Logout error:', error);
    } finally {
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      delete axios.defaults.headers.common['Authorization'];
      setIsAuthenticated(false);
      setUser(null);
    }
  };

  return (
    <Router>
      <div className="app">
        <Navbar isAuthenticated={isAuthenticated} logout={logout} user={user} />
        <div className="container">
          <Routes>
            <Route path="/" element={<Navigate to="/stakeout" replace />} />
            <Route path="/stakeout" element={<PrivateRoute><StakeOutBet /></PrivateRoute>} />
            <Route path="/login" element={!isAuthenticated ? <LoginComponent /> : <Navigate to="/stakeout" replace />} />
            <Route path="/register" element={!isAuthenticated ? <RegisterComponent /> : <Navigate to="/stakeout" replace />} />
            <Route path="/profile" element={<PrivateRoute><ProfileComponent /></PrivateRoute>} />
            <Route path="/history" element={<PrivateRoute><BetHistoryComponent /></PrivateRoute>} />
            <Route path="*" element={<Navigate to="/" replace />} />
          </Routes>
        </div>

        <footer className="footer">
          <p>&copy; {new Date().getFullYear()} Stake Out Bet. All rights reserved.</p>
        </footer>
      </div>
    </Router>
  );
};

export default App;
