import React from 'react';

const GameGraph = ({ graphPoints, multiplier, dangerLevel, getDangerColor, graphCalculations, svgRef, autoCashout }) => {
  if (!graphPoints.length || !graphCalculations) return null;

  const { linePath, areaPath, gridLines, lastPoint, maxTime, maxValue } = graphCalculations;

  // Calculate auto cash-out line position if enabled
  const autoCashoutLineY = autoCashout > 0 
    ? 100 - (Math.min(autoCashout, maxValue) / maxValue) * 95 
    : null;

  // Determine if we're getting close to auto cash-out (within 20%)
  const isApproachingAutoCashout = autoCashout > 0 && multiplier >= autoCashout * 0.8;

  return (
    <svg
      ref={svgRef}
      width="100%"
      height="100%"
      viewBox="0 0 100 100"
      preserveAspectRatio="none"
      className="absolute inset-0"
    >
      {/* Grid Lines */}
      {gridLines.map((line, i) => (
        <g key={`h-grid-${i}`}>
          <line x1="0" y1={line.y} x2="100" y2={line.y} stroke="#374151" strokeWidth="0.2" strokeDasharray="2,2" />
          <text x="1" y={line.y - 1} fill="#9CA3AF" fontSize="3">{line.value.toFixed(1)}x</text>
        </g>
      ))}

      {/* Auto Cash-Out Indicator */}
      {autoCashoutLineY !== null && (
        <g>
          <line
            x1="0"
            y1={autoCashoutLineY}
            x2="100"
            y2={autoCashoutLineY}
            stroke="#10B981"
            strokeWidth="0.5"
            strokeDasharray="1,1"
          />
          <text x="2" y={autoCashoutLineY - 1.5} fill="#10B981" fontSize="3">Auto {autoCashout.toFixed(2)}x</text>
        </g>
      )}

      {/* Grid Lines */}
      {gridLines.map((line, i) => (
        <line
          key={`h-grid-${i}`}
          x1="0"
          y1={line.y}
          x2="100"
          y2={line.y}
          stroke="#374151"
          strokeWidth="0.2"
          strokeDasharray="2,2"
        />
      ))}

      {/* Area under curve */}
      <path d={areaPath} fill={getDangerColor()} fillOpacity="0.2" />

      {/* Graph Line */}
      <path d={linePath} fill="none" stroke={getDangerColor()} strokeWidth="0.6" strokeLinecap="round" />

      {/* Current Indicator Point */}
      <circle cx={lastPoint.cx} cy={lastPoint.cy} r="1.2" fill={getDangerColor()} className={dangerLevel === 'extreme' || isApproachingAutoCashout ? 'animate-pulse' : ''} />
    </svg>
  );
};

export default GameGraph;
